"use strict";

var LArray = 
{
    __name__ : "LArray", 
    
    // 移除一个数组重复项
    unique : function (data)
    {
        var i = 0, j = data.length, k;
        var ret = [], exist = false;
        
        for (; i < j; i++)
        {
            for (k = i + 1; k < j; k++)
            {
                if (data[i] === data[k])
                {
                    exist = true;
                    break;
                }
            }
            
            if (exist)
            {
                exist = false;
            }
            else
            {
                ret.push(data[i]);
            }
        }
        
        return ret;
    }, 
    
    // 创建一个包含指定范围单元的数组 
    range : function (low, high, step)
    {
        var ret = [];
        var direction = 1;
        
        if (low > high)
        {
            low ^= high;
            high ^= low;
            low ^= high;
            direction = -1;
        }
        
        step = undefined === step ? 1 : step | 0;
        
        if (step >= high)
        {
            return ret;
        }
        
        var i = 0, len = Math.ceil((high - low + 1) / step);
        
        for (; i < len; i++)
        {
            if (~direction)
            {
                ret[i] = low;
                low += step;
            }
            else
            {
                ret[i] = high;
                high -= step;
            }
        }
        
        return ret;
    }
};

module.exports = LArray;