"use strict";

var LMath = 
{
    __name__ : "LMath", 
    
    /**
     * @brief rand 产生一个随机整数
     * @param {number} min 最小随机数
     * @param {number} max 最大随机数
     * @return {number} 返回 min - max 之间的随机整数
    **/
    rand : function (min, max)
    {
        if(isNaN(min) || isNaN(max))
        {
            min = 0;
            max = 0xFFFFFFFF;
        }
        
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }
};

module.exports = LMath;