"use strict";

module.exports = Quit;

function Quit (socket)
{
    this._socket = socket;
}

Quit.prototype = 
{
    constructor : Quit, 
    
    RETURN_STATE : 
    {
        ERROR : -1, 
        PART : 0, 
        COMPLETE : 1
    }, 
    
    parse : function (chunk, i, len)
    {
        // quit\r\n
        this._socket.destroy();
        return this.RETURN_STATE.COMPLETE;
    }
};