"use strict";

module.exports = Stats;

var Data = Dew("use", "Server.Cache.Data");

function Stats (socket)
{
    this._socket = socket;
}

Stats.prototype = 
{
    constructor : Stats, 
    
    RETURN_STATE : 
    {
        ERROR : -1, 
        PART : 0, 
        COMPLETE : 1
    }, 
    
    parse : function (chunk, i, len)
    {
        // stats\r\n
        
        Data.stats(this._socket);
        
        return this.RETURN_STATE.COMPLETE;
    }
};