"use strict";

module.exports = Version;

function Version (socket)
{
    this._socket = socket;
}

Version.prototype = 
{
    constructor : Version, 
    
    RETURN_STATE : 
    {
        ERROR : -1, 
        PART : 0, 
        COMPLETE : 1
    }, 
    
    parse : function (chunk, i, len)
    {
        // version\r\n
        
        this._socket.write("VERSION 0.1\r\n");
        return this.RETURN_STATE.COMPLETE;
    }
};